        """Test full document generation."""
        doc = visualizer.generate_architecture_doc()
        
        # Check header
        assert "# üèóÔ∏è Agent Architecture" in doc
        
        # Check all major sections exist
        assert "## üì¶ State Slices" in doc
        assert "## üéØ System Hierarchy" in doc
        assert "## ‚ö° Trigger Hierarchy" in doc
        assert "## üìö Nodes Reference" in doc
        
        # Check footer
        assert "Generated by `agent-contracts`" in doc
    
