query:
mutation policyZtnaAlwaysOnAddRule ( $accountId:ID! $ztnaAlwaysOnPolicyMutationInput:ZtnaAlwaysOnPolicyMutationInput $ztnaAlwaysOnAddRuleInput:ZtnaAlwaysOnAddRuleInput!) {
	policy ( accountId:$accountId ) {
		ztnaAlwaysOn ( input:$ztnaAlwaysOnPolicyMutationInput  )  {
			addRule ( input:$ztnaAlwaysOnAddRuleInput   ) {
				rule {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						source {
							user {
								id
								name
							}
							usersGroup {
								id
								name
							}
						}
						platform
						devicePostureProfile {
							id
							name
						}
						action
						allowUserBypass
						bypassDuration {
							time
							unit
						}
						allowFailOpen
						antiTamperMode
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
