query:
query policyClientConnectivityPolicy ( $accountId:ID! $clientConnectivityPolicyInput:ClientConnectivityPolicyInput) {
	policy ( accountId:$accountId ) {
		clientConnectivity {
			policy ( input:$clientConnectivityPolicyInput   ) {
				enabled
				rules {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						source {
							user {
								id
								name
							}
							usersGroup {
								id
								name
							}
						}
						confidenceLevel
						platform
						country {
							id
							name
						}
						sourceRange {
							globalIpRange {
								id
								name
							}
						}
						device {
							id
							name
						}
						connectionOrigin
						action
					}
					properties
				}
				sections {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				audit {
					publishedTime
					publishedBy
				}
				revision {
					id
					name
					description
					changes
					createdTime
					updatedTime
				}
			}
		}
	}	
}
