query:
query policyTerminalServerPolicy ( $accountId:ID! $terminalServerPolicyInput:TerminalServerPolicyInput) {
	policy ( accountId:$accountId ) {
		terminalServer {
			policy ( input:$terminalServerPolicyInput   ) {
				enabled
				rules {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						allowedHostIP {
							id
							name
						}
						excludeTraffic {
							id
							name
						}
					}
					properties
				}
				sections {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				audit {
					publishedTime
					publishedBy
				}
				revision {
					id
					name
					description
					changes
					createdTime
					updatedTime
				}
			}
		}
	}	
}
