query:
mutation policyRemotePortFwdUpdateRule ( $accountId:ID! $remotePortFwdPolicyMutationInput:RemotePortFwdPolicyMutationInput $remotePortFwdUpdateRuleInput:RemotePortFwdUpdateRuleInput!) {
	policy ( accountId:$accountId ) {
		remotePortFwd ( input:$remotePortFwdPolicyMutationInput  )  {
			updateRule ( input:$remotePortFwdUpdateRuleInput   ) {
				rule {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						forwardIcmp
						externalIp {
							id
							name
						}
						externalPortRange {
							from
							to
						}
						internalIp
						internalPortRange {
							from
							to
						}
						remoteIPs {
							ip
							ipRange {
								from
								to
							}
							subnet
							globalIpRange {
								id
								name
							}
						}
						restrictionType
						tracking {
							enabled
							frequency
							subscriptionGroup {
								id
								name
							}
							webhook {
								id
								name
							}
							mailingList {
								id
								name
							}
						}
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
