query:
mutation policyTerminalServerUpdateSection ( $accountId:ID! $terminalServerPolicyMutationInput:TerminalServerPolicyMutationInput $policyUpdateSectionInput:PolicyUpdateSectionInput!) {
	policy ( accountId:$accountId ) {
		terminalServer ( input:$terminalServerPolicyMutationInput  )  {
			updateSection ( input:$policyUpdateSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
