query:
mutation policyZtnaAlwaysOnCreatePolicyRevision ( $accountId:ID! $ztnaAlwaysOnPolicyMutationInput:ZtnaAlwaysOnPolicyMutationInput $policyCreateRevisionInput:PolicyCreateRevisionInput!) {
	policy ( accountId:$accountId ) {
		ztnaAlwaysOn ( input:$ztnaAlwaysOnPolicyMutationInput  )  {
			createPolicyRevision ( input:$policyCreateRevisionInput   ) {
				policy {
					enabled
					rules {
						audit {
							updatedTime
							updatedBy
						}
						rule {
							id
							name
							description
							index
							section {
								id
								name
							}
							enabled
							source {
								user {
									id
									name
								}
								usersGroup {
									id
									name
								}
							}
							platform
							devicePostureProfile {
								id
								name
							}
							action
							allowUserBypass
							bypassDuration {
								time
								unit
							}
							allowFailOpen
							antiTamperMode
						}
						properties
					}
					sections {
						audit {
							updatedTime
							updatedBy
						}
						section {
							id
							name
						}
						properties
					}
					audit {
						publishedTime
						publishedBy
					}
					revision {
						id
						name
						description
						changes
						createdTime
						updatedTime
					}
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
