query siteSiteGeneralDetails ( $accountId:ID! $siteRefInput:SiteRefInput!) {
	site ( accountId:$accountId ) {
		siteGeneralDetails ( site:$siteRefInput  )  {
			site {
				id
				name
			}
			siteType
			description
			siteLocation {
				countryCode
				stateCode
				cityName
				timezone
				address
			}
			preferredPopLocation {
				primary {
					id
					name
				}
				secondary {
					id
					name
				}
				preferredOnly
			}
		}
	}	
}
