query:
mutation policyTerminalServerAddRule ( $accountId:ID! $terminalServerPolicyMutationInput:TerminalServerPolicyMutationInput $terminalServerAddRuleInput:TerminalServerAddRuleInput!) {
	policy ( accountId:$accountId ) {
		terminalServer ( input:$terminalServerPolicyMutationInput  )  {
			addRule ( input:$terminalServerAddRuleInput   ) {
				rule {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						allowedHostIP {
							id
							name
						}
						excludeTraffic {
							id
							name
						}
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
