query:
mutation accountManagementUpdateAccount ( $accountId:ID! $updateAccountInput:UpdateAccountInput!) {
	accountManagement ( accountId:$accountId ) {
		updateAccount ( input:$updateAccountInput  )  {
			id
			name
			description
			timeZone
			type
			tenancy
			audit {
				createdBy
				createdTime
			}
			plan
			status
		}
	}	
}
