mutation adminUpdateServicePrincipalAdmin ( $accountId:ID! $adminID:ID! $updateServicePrincipalAdminInput:UpdateServicePrincipalAdminInput!) {
	admin ( accountId:$accountId ) {
		updateServicePrincipalAdmin ( adminID:$adminID  input:$updateServicePrincipalAdminInput  )  {
			adminID
		}
	}	
}
