query:
mutation enterpriseDirectoryCreateLocation ( $accountId:ID! $enterpriseDirectoryCreateLocationInput:EnterpriseDirectoryCreateLocationInput!) {
	enterpriseDirectory ( accountId:$accountId ) {
		createLocation ( input:$enterpriseDirectoryCreateLocationInput  )  {
			location {
				id
				account {
					id
					name
				}
				type
				name
				businessUnit
				description
				details {
					companyName
					postalAddress {
						street
						cityName
						country {
							id
							name
						}
						stateName
						zipCode
						addressValidated
					}
					vatId
					contact {
						name
						phone
						email
					}
					shippingLocation
				}
				archived
				audit {
					updatedTime
					updatedBy {
						id
						name
					}
				}
			}
		}
	}	
}
