query:
mutation policyClientConnectivityAddRule ( $accountId:ID! $clientConnectivityPolicyMutationInput:ClientConnectivityPolicyMutationInput $clientConnectivityAddRuleInput:ClientConnectivityAddRuleInput!) {
	policy ( accountId:$accountId ) {
		clientConnectivity ( input:$clientConnectivityPolicyMutationInput  )  {
			addRule ( input:$clientConnectivityAddRuleInput   ) {
				rule {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						source {
							user {
								id
								name
							}
							usersGroup {
								id
								name
							}
						}
						confidenceLevel
						platform
						country {
							id
							name
						}
						sourceRange {
							globalIpRange {
								id
								name
							}
						}
						device {
							id
							name
						}
						connectionOrigin
						action
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
