query:
mutation policyWanNetworkUpdateSection ( $accountId:ID! $wanNetworkPolicyMutationInput:WanNetworkPolicyMutationInput $policyUpdateSectionInput:PolicyUpdateSectionInput!) {
	policy ( accountId:$accountId ) {
		wanNetwork ( input:$wanNetworkPolicyMutationInput  )  {
			updateSection ( input:$policyUpdateSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
