query:
mutation hardwareUpdateHardwareShipping ( $accountId:ID! $updateHardwareShippingInput:UpdateHardwareShippingInput!) {
	hardware ( accountId:$accountId ) {
		updateHardwareShipping ( input:$updateHardwareShippingInput  )  {
			items {
				id
				account {
					id
					name
				}
				siteCountryName
				productType
				model
				serialNumber
				macAddress
				poNumber
				quoteId
				licenseId
				licenseStartDate
				shippingTracking {
					shippingStatus
					shippingDate
					trackingUrl
					trackingNumber
					carrier
				}
				shippingDetail {
					companyName
					street
					cityName
					countryName
					stateName
					zipCode
					incoterms
					instruction
					vatId
					contact {
						name
						phone
						email
					}
					comment
					powerCable
				}
				validation {
					completed
					incompleteReason
					addressValidationStatus
				}
				lastModified
				locationId
			}
			pageInfo {
				total
			}
		}
	}	
}
