query:
mutation policyWanFirewallUpdateSection ( $accountId:ID! $wanFirewallPolicyMutationInput:WanFirewallPolicyMutationInput $policyUpdateSectionInput:PolicyUpdateSectionInput!) {
	policy ( accountId:$accountId ) {
		wanFirewall ( input:$wanFirewallPolicyMutationInput  )  {
			updateSection ( input:$policyUpdateSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
