query:
mutation policyApplicationControlMoveSection ( $accountId:ID! $applicationControlPolicyMutationInput:ApplicationControlPolicyMutationInput $policyMoveSectionInput:PolicyMoveSectionInput!) {
	policy ( accountId:$accountId ) {
		applicationControl ( input:$applicationControlPolicyMutationInput  )  {
			moveSection ( input:$policyMoveSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
