query:
mutation policyClientConnectivityDiscardPolicyRevision ( $accountId:ID! $clientConnectivityPolicyMutationInput:ClientConnectivityPolicyMutationInput $policyDiscardRevisionInput:PolicyDiscardRevisionInput) {
	policy ( accountId:$accountId ) {
		clientConnectivity ( input:$clientConnectivityPolicyMutationInput  )  {
			discardPolicyRevision ( input:$policyDiscardRevisionInput   ) {
				policy {
					enabled
					rules {
						audit {
							updatedTime
							updatedBy
						}
						rule {
							id
							name
							description
							index
							section {
								id
								name
							}
							enabled
							source {
								user {
									id
									name
								}
								usersGroup {
									id
									name
								}
							}
							confidenceLevel
							platform
							country {
								id
								name
							}
							sourceRange {
								globalIpRange {
									id
									name
								}
							}
							device {
								id
								name
							}
							connectionOrigin
							action
						}
						properties
					}
					sections {
						audit {
							updatedTime
							updatedBy
						}
						section {
							id
							name
						}
						properties
					}
					audit {
						publishedTime
						publishedBy
					}
					revision {
						id
						name
						description
						changes
						createdTime
						updatedTime
					}
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
