query:
mutation policyTerminalServerAddSection ( $accountId:ID! $terminalServerPolicyMutationInput:TerminalServerPolicyMutationInput $policyAddSectionInput:PolicyAddSectionInput!) {
	policy ( accountId:$accountId ) {
		terminalServer ( input:$terminalServerPolicyMutationInput  )  {
			addSection ( input:$policyAddSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
