query:
mutation policyZtnaAlwaysOnAddSection ( $accountId:ID! $ztnaAlwaysOnPolicyMutationInput:ZtnaAlwaysOnPolicyMutationInput $policyAddSectionInput:PolicyAddSectionInput!) {
	policy ( accountId:$accountId ) {
		ztnaAlwaysOn ( input:$ztnaAlwaysOnPolicyMutationInput  )  {
			addSection ( input:$policyAddSectionInput   ) {
				section {
					audit {
						updatedTime
						updatedBy
					}
					section {
						id
						name
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
