query:
mutation policyDynamicIpAllocationUpdateRule ( $accountId:ID! $dynamicIpAllocationPolicyMutationInput:DynamicIpAllocationPolicyMutationInput $dynamicIpAllocationUpdateRuleInput:DynamicIpAllocationUpdateRuleInput!) {
	policy ( accountId:$accountId ) {
		dynamicIpAllocation ( input:$dynamicIpAllocationPolicyMutationInput  )  {
			updateRule ( input:$dynamicIpAllocationUpdateRuleInput   ) {
				rule {
					audit {
						updatedTime
						updatedBy
					}
					rule {
						id
						name
						description
						index
						section {
							id
							name
						}
						enabled
						source {
							user {
								id
								name
							}
							usersGroup {
								id
								name
							}
						}
						platform
						country {
							id
							name
						}
						range {
							globalIpRange {
								id
								name
							}
						}
					}
					properties
				}
				status
				errors {
					errorMessage
					errorCode
				}
			}
		}
	}	
}
