query:
query catalogsCatalogApplication ( $accountId:ID! $applicationRefInput:ApplicationRefInput!) {
	catalogs ( accountId:$accountId ) {
		catalogApplication ( input:$applicationRefInput  )  {
			id
			name
			descriptionSummary
			description
			category {
				id
				name
			}
			complianceAttributes {
				iso27001
				sox
				hippa
				soc1
				soc2
				soc3
				isae3402
				pciDss
			}
			securityAttributes {
				mfa
				encryptionAtRest
				auditTrail
				rbac
				rememberPassword
				sso
				trustedCertificate
				tlsEnforcement
				httpSecurityHeaders
			}
			ipoStatus
			website
			originCountry {
				id
				name
			}
			region
			city
			risk
			numOfEmployees
			capability
			activity {
				id
				name
				fields {
					id
					name
					possibleOperators
					possibleValues
				}
			}
			tenantActivity {
				id
				name
				fields {
					id
					name
					possibleOperators
					possibleValues
				}
			}
			type
			sanctioned
			recentlyAdded
			standardPorts {
				port
				portRange {
					from
					to
				}
				protocol
			}
			aiSecurity {
				risk
				scope
				dataUsagePolicy {
					type
					description
					reference
					referenceType
					referenceUrl
				}
			}
		}
	}	
}
