def create_new_project(
    project_name: str,
    project_type: str = "library",
    author: str = "",
    description: str = "",
    license_type: str = "mit",
    dry_run: bool = False,
    verbose: bool = False,
) -> None:
    """Create a new Python project.

    Args:
        project_name: Name of the project to create.
        project_type: Type of project (library, cli, script).
        author: Author name.
        description: Project description.
        license_type: License type.
        dry_run: Preview without creating files.
        verbose: Show verbose output.
    """
    # Validate inputs
    try:
        project_name = validate_project_name(project_name)
        project_type = validate_project_type(project_type)
        license_type = validate_license_type(license_type)
    except ValidationError as e:
        print(f"[red]Error: {e}[/]")
        raise SystemExit(1)

    # Generate template context
    context = generate_context(
        project_name=project_name,
        project_type=project_type,
        author=author,
        description=description,
        license_type=license_type,
    )

    # Determine output path
    output_path = Path.cwd() / project_name

    if verbose:
        print(f"Output path: {output_path}")
        print(f"Project type: {project_type}")

    # Show summary
    print(Panel(
        f"[bold]Creating new {project_type} project:[/] [cyan]{project_name}[/]\n"
        f"[dim]Author:[/] {context['author']}\n"
        f"[dim]License:[/] {context['license']}\n"
        f"[dim]Python:[/] {', '.join(context['python_versions'])}",
        title="Gentem",
        expand=False,
    ))

    if dry_run:
        print("[yellow]DRY RUN - No files will be created[/]")
        print(f"\nProject would be created at: {output_path}")
        print("\nFiles that would be created:")
        for item in get_project_files(project_name, project_type):
            print(f"  - {item}")
        return

    # Create project files
    try:
        create_project_files(
            project_name=project_name,
            project_type=project_type,
            context=context,
            output_path=output_path,
            license_content=get_license_content(license_type, context),
        )
        print(f"\n[green]âœ“ Project '{project_name}' created successfully![/]")
        print(f"\nNext steps:")
        print(f"  cd {project_name}")
        if project_type == "library":
            print(f"  pip install -e .")
        elif project_type == "cli":
            print(f"  pip install -e .")
            print(f"  {project_name} --help")
        print(f"  git init")
    except Exception as e:
        print(f"[red]Error creating project: {e}[/]")
        raise SystemExit(1)


def get_project_files(project_name: str, project_type: str) -> list[str]:
    """Get list of files that would be created for a project.

    Args:
        project_name: Name of the project.
        project_type: Type of project.

    Returns:
        List of file paths.
    """
    slug = generate_slug(project_name)
    files = [
        f"{project_name}/",
        f"{project_name}/pyproject.toml",
        f"{project_name}/README.md",
        f"{project_name}/LICENSE",
        f"{project_name}/.gitignore",
    ]

    if project_type == "library":
        files.extend([
            f"{project_name}/app/",
            f"{project_name}/app/__init__.py",
            f"{project_name}/tests/",
            f"{project_name}/tests/__init__.py",
            f"{project_name}/tests/test_{slug}.py",
        ])
    elif project_type == "cli":
        files.extend([
            f"{project_name}/app/",
            f"{project_name}/app/__init__.py",
            f"{project_name}/app/cli.py",
            f"{project_name}/app/main.py",
        ])
    elif project_type == "script":
        files.extend([
            f"{project_name}/{slug}.py",
        ])

    return files


def create_project_files(
    project_name: str,
    project_type: str,
    context: dict,
    output_path: Path,
    license_content: str,
) -> None:
    """Create all project files.

    Args:
        project_name: Name of the project.
        project_type: Type of project.
        context: Template context.
        output_path: Path to create the project at.
        license_content: License file content.
    """
    slug = generate_slug(project_name)

    # Create directories
    output_path.mkdir(parents=True, exist_ok=False)

    # Create pyproject.toml
    scripts_line = f'{slug} = "{slug}.main:main"' if project_type == "cli" else ""
    pyproject_content = f'''[build-system]
requires = ["setuptools>=61.0", "wheel"]
build-backend = "setuptools.build_meta"

[project]
name = "{slug}"
version = "{context["version"]}"
description = "{context["description"]}"
readme = "README.md"
license = {{text = "{context["license"]}"}}
requires-python = ">={context["python_version"]}"
authors = [{{name = "{context["author"]}", email = "{context["email"]}"}}]
keywords = ["{slug}"]
classifiers = [
    "Development Status :: 3 - Alpha",
    "Intended Audience :: Developers",
    "License :: OSI Approved :: {context["license"]} License",
    "Programming Language :: Python :: 3",
]
dependencies = []

[project.optional-dependencies]
dev = [
    "pytest>=7.4.0",
    "black>=23.0.0",
    "ruff>=0.1.0",
    "mypy>=1.5.0",
]

[project.scripts]
{str(scripts_line)}

[tool.setuptools.packages.find]
where = ["app"]

[tool.pytest.ini_options]
testpaths = ["tests"]
python_files = ["test_*.py"]
addopts = "-v --tb=short"

[tool.black]
line-length = 100
target-version = ["py39", "py310", "py311", "py312"]

[tool.ruff]
line-length = 100
select = ["E", "W", "F", "I", "B", "C4", "UP"]
ignore = ["E501", "B008", "C901"]
'''

    # Create README.md
    readme_content = f'''# {project_name}

{context["description"]}

## Installation

```bash
pip install {slug}
```

## Usage

```python
import {slug}
```

## Development

```bash
# Install development dependencies
pip install -e ".[dev]"

# Run tests
pytest

# Format code
black .
ruff check --fix .
```

## License

This project is licensed under the {context["license"]} License - see the [LICENSE](LICENSE) file for details.
'''

    # Create .gitignore
    gitignore_content = '''# Byte-compiled / optimized / DLL files
__pycache__/
*.py[cod]
*$py.class

# C extensions
*.so

# Distribution / packaging
.Python
build/
develop-eggs/
dist/
downloads/
eggs/
.eggs/
lib/
lib64/
parts/
sdist/
var/
wheels/
pip-wheel-metadata/
share/python-wheels/
*.egg-info/
.installed.cfg
*.egg
MANIFEST

# PyInstaller
*.manifest
*.spec

# Installer logs
pip-log.txt
pip-delete-this-directory.txt

# Unit test / coverage reports
htmlcov/
.tox/
.nox/
.coverage
.coverage.*
.cache
nosetests.xml
coverage.xml
*.cover
*.py,cover
.hypothesis/
.pytest_cache/

# Translations
*.mo
*.pot

# Django stuff:
*.log
local_settings.py
db.sqlite3
db.sqlite3-journal

# Flask stuff:
instance/
.webassets-cache

# Scrapy stuff:
.scrapy

# Sphinx documentation
docs/_build/

# PyBuilder
target/

# Jupyter Notebook
.ipynb_checkpoints

# IPython
profile_default/
ipython_config.py

# pyenv
.python-version

# pipenv
Pipfile.lock

# PEP 582
__pypackages__/

# Celery stuff
celerybeat-schedule
celerybeat.pid

# SageMath parsed files
*.sage.py

# Environments
.env
.venv
env/
venv/
ENV/
env.bak/
venv.bak/

# Spyder project settings
.spyderproject
.spyproject

# Rope project settings
.ropeproject

# mkdocs documentation
/site

# mypy
.mypy_cache/
.dmypy.json
dmypy.json

# Pyre type checker
.pyre/

# IDEs
.vscode/
.idea/
*.swp
*.swo
*~

# OS
.DS_Store
Thumbs.db
'''

    # Write common files
    (output_path / "pyproject.toml").write_text(pyproject_content, encoding="utf-8")
    (output_path / "README.md").write_text(readme_content, encoding="utf-8")
    (output_path / ".gitignore").write_text(gitignore_content, encoding="utf-8")
    if license_content:
        (output_path / "LICENSE").write_text(license_content, encoding="utf-8")

    # Create type-specific files
    if project_type == "library":
        app_dir = output_path / "app"
        app_dir.mkdir(parents=True, exist_ok=True)
        (app_dir / "__init__.py").write_text(f'"""{project_name} - {context["description"]}."""\n', encoding="utf-8")

        tests_dir = output_path / "tests"
        tests_dir.mkdir(parents=True, exist_ok=True)
        (tests_dir / "__init__.py").write_text('"""Tests for app."""\n', encoding="utf-8")
        (tests_dir / f"test_{slug}.py").write_text(f'''"""Tests for {slug}."""

import pytest

from app import __version__


def test_version():
    """Test that version is set correctly."""
    assert __version__ == "{context["version"]}"


def test_import():
    """Test that the package can be imported."""
    import app
    assert app is not None
''', encoding="utf-8")

    elif project_type == "cli":
        app_dir = output_path / "app"
        app_dir.mkdir(parents=True, exist_ok=True)

        (app_dir / "__init__.py").write_text(f'"""{project_name} - {context["description"]}."""\n', encoding="utf-8")
        (app_dir / "cli.py").write_text(f'''"""CLI commands for {project_name}."""

import typer

app = typer.Typer()


@app.command()
def hello(name: str = "World") -> None:
    """Say hello to someone."""
    print(f"Hello, {{name}}!")


@app.command()
def version() -> None:
    """Show the version."""
    print("{context["version"]}")
''', encoding="utf-8")
        (app_dir / "main.py").write_text(f'''"""Main entry point for {project_name}."""

from app.cli import app


def main() -> None:
    """Entry point for the CLI."""
    app()
''', encoding="utf-8")

    elif project_type == "script":
        (output_path / f"{slug}.py").write_text(f'''#!/usr/bin/env python3
"""{project_name} - {context["description"]}."""

def main() -> None:
    """Main entry point."""
    print("Hello from {project_name}!")


if __name__ == "__main__":
    main()
''', encoding="utf-8")
