"""Authentication routes."""

from datetime import datetime, timedelta

from fastapi import APIRouter, Depends, HTTPException, status
from sqlalchemy.ext.asyncio import AsyncSession

from app.auth import (
    authenticate_user,
    create_access_token,
    generate_api_key,
    get_user_by_email,
)
from app.database import get_db
from app.models import ApiKey
from app.schemas import LoginRequest, LoginResponse, TokenRequest, TokenResponse
from app.settings import get_settings

router = APIRouter(prefix="/api/v1/auth", tags=["auth"])
settings = get_settings()


@router.post("/login", response_model=LoginResponse)
async def login(
    request: LoginRequest,
    db: AsyncSession = Depends(get_db),
):
    """Web UI login - returns a JWT token."""
    user = await authenticate_user(db, request.email, request.password)
    if not user:
        raise HTTPException(
            status_code=status.HTTP_401_UNAUTHORIZED,
            detail="Invalid email or password",
        )

    access_token = create_access_token(user.id)
    return LoginResponse(access_token=access_token)


@router.post("/token", response_model=TokenResponse)
async def exchange_token(
    request: TokenRequest,
    db: AsyncSession = Depends(get_db),
):
    """Exchange a one-time code for an API key.

    The code is currently the user's email - in production, this would be
    a short-lived code generated by the web UI after login.
    """
    # For now, treat the code as the user's email
    # In production: look up a one-time code table
    user = await get_user_by_email(db, request.code)
    if not user:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail="Invalid code",
        )

    # Generate API key
    api_key, key_hash = generate_api_key()
    expires_at = datetime.utcnow() + timedelta(days=settings.api_key_expire_days)

    # Store the key hash
    db_key = ApiKey(
        user_id=user.id,
        key_hash=key_hash,
        expires_at=expires_at,
    )
    db.add(db_key)
    await db.flush()

    return TokenResponse(
        api_key=api_key,
        expires_at=expires_at,
        user_email=user.email,
        user_name=user.name,
    )
