packages = \
['pipen_cli_gbatch']

package_data = \
{'': ['*']}

install_requires = \
['panpath[async-gs]>=0.4.9,<0.5.0',
 'pipen-poplog>=1.1,<2.0',
 'pipen>=1.1.2,<2.0.0']

