def normalize_kit_name(raw_name: str) -> str:
    """
    Vrátí standardizovaný název kitu podle textu, který vrací GeneMapper.
    """
    if not raw_name:
        return "26plex QS"  # výchozí kit


    if "Fusion_6C" in raw_name:
        return "Fusion 6C"
    elif "Fusion" in raw_name:
        return "Fusion"
    elif "GlobalFiler" in raw_name:
        return "GlobalFiler"
    elif "VeriFiler" in raw_name:
        return "Verifiler plus"
    elif "ESS" in raw_name:
        return "ESS SE QS"
    elif "Y28" in raw_name:
        return "Y28"
    elif "Y23" in raw_name:
        return "Y23"
    elif "Yfiler" in raw_name:
        return "Yfiler"
    elif "ESX" in raw_name:
        return "ESX"
    elif "ESI" in raw_name:
        return "ESI"
    elif "NGM" in raw_name:
        return "NGM Detect"
    elif "26plex" in raw_name:
        return "26plex QS"
    elif "24plex" in raw_name:
        return "24plex"
    elif "A27" in raw_name:
        return "A27"
    elif "A33" in raw_name:
        return "A33"
